import './Training.css'
import { useState } from 'react'
import Trainings from '../../Data/Training'



const Training = () => {


  const [showMenu, setShowMenu] = useState(false)

  const handleShowMenu = (titulo) => {
   const objeto =  Trainings.find((item) => item.title === titulo )
    setShowMenu(objeto)
  }
  return (
    <div id='training' className='training'>
      <p className='letters-head-section'>Formaci√≥n</p>
      <header className='training-header'>
        <menu className='training-menu'>
          <ul>
            {Trainings.map((objeto) => (
              <li className='training-menu-li'>
                <button onClick={handleShowMenu(objeto.title)} className='training-menu-li-button'>
                  {title}
                </button>
              </li>
            ))}
          </ul>
        </menu>
        <section>
          {Trainings.map(({title}) => (
            <article>{title}</article>
          ))}
        </section>
      </header>
    </div>
  )
}

export default Training
