import emailjs from 'emailjs-com'
import { useState } from 'react'

import './EmailSend.css'

function EmailSend() {
  const [name, setName] = useState('')
  const [mail, setMail] = useState('')
  const [message, setMessage] = useState('')
  const [nameError, setNameError] = useState(false)
  const [mailError, setMailError] = useState(false)
  const [messageError, setMessageError] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (name.trim() === '') {
      setNameError(true)
      return
    } else {
      setNameError(false)
    }

    if (mail.trim() === '') {
      setMailError(true)
      return
    } else {
      setMailError(false)
    }

    if (message.trim() === '') {
      setMessageError(true)
      return
    } else {
      setMessageError(false)
    }


  }

  function handleName(e) {
    setName(e.target.value)
  }

  function handleMail(e) {
    setMail(e.target.value)
  }

  function handleMessage(e) {
    setMessage(e.target.value)
  }

  return (
    <div className="email">
      {/* Resto del código del componente */}

      <form className="form-message" onSubmit={handleSubmit}>
        <div className="input-container">
          <div className="form">
            <input
              type="text"
              value={name}
              onChange={handleName}
              name="nombre"
              id="nombre"
              placeholder="  Tú Nombre"
            />
            {nameError && <h1 className="error">¡El campo Nombre está vacío!</h1>}
          </div>

          <div className="form">
            <input
              type="text"
              value={mail}
              onChange={handleMail}
              name="email"
              id="nombre"
              placeholder="  example@example.com"
            />
            {mailError && <h1 className="error">¡El campo Email está vacío!</h1>}
          </div>

          <div className="form-text">
            <textarea
              type="text"
              value={message}
              onChange={handleMessage}
              name="mensaje"
              id="mensaje"
              placeholder="  Déjame tú mensaje..."
            ></textarea>
            {messageError && <h1 className="error">¡El campo Mensaje está vacío!</h1>}
          </div>

          <div className="email-send">
            <button type="submit" id="email-send">
              Enviar mensaje
            </button>
          </div>
        </div>
      </form>
    </div>
  )
}

export default EmailSend
